mapper.select:
/* promotion.exhibition.selectExhibitionListPagingCount - 기획전목록 조회결과 개수*/
SELECT COUNT(*)
FROM TP_PROMOTION
WHERE SITE_NO = #{siteNo}
AND DEL_YN = 'N'
AND PRMT_TYPE = 'EX'

/* promotion.exhibition.selectExhibitionListPagingTotalCount - 총 기획전목록 개수*/
SELECT COUNT(*)
FROM TP_PROMOTION
WHERE SITE_NO = #{siteNo}
  AND DEL_YN = 'N'
  AND PRMT_TYPE = 'EX'

		SELECT NVL(MAX(PRMT_DISPZONE_NO), 0) + 1 AS PRMT_DISPZONE_NO
		FROM TP_PROMOTION_DISPZONE
        WHERE PRMT_NO = #{prmtNo}

mapper.update:

        /* promotion.exhibition.updateExhibitionDispzone - 전시존 정보  수정*/
		UPDATE TP_PROMOTION_DISPZONE 
		SET		
                DISPZONE_NM                 = #{dispzoneNm},


                USE_YN              = #{useYn},

                UPD_DTTM                = sysdate,
                UPDR_NO                 = #{updrNo}
		WHERE PRMT_NO = #{prmtNo} 
			AND PRMT_DISPZONE_NO = #{prmtDispzoneNo}

mapper.delete:
/* promotion.exhibition.deleteExhibitionTargetGoods - 기획전대상 상품 지우기*/
    DELETE  FROM TP_PROMOTION_TARGET_GOODS
    WHERE PRMT_NO = #{prmtNo}

        /* promotion.exhibition.deleteExhibitionDispzone - 전시존 삭제*/
		DELETE FROM TP_PROMOTION_DISPZONE
		WHERE PRMT_NO = #{prmtNo}

mapper.select:
/* promotion.exhibition.selectExhibitionTargetTotal - 기획전대상 전체조회 : 기획전대상 중복방지*/ SELECT TPTG.GOODS_NO, TP.PRMT_NM, TP.PRMT_NO FROM TP_PROMOTION_TARGET_GOODS TPTG INNER JOIN TP_PROMOTION TP ON TPTG.PRMT_NO = TP.PRMT_NO WHERE TPTG.USE_YN = 'Y' AND TP.DEL_YN = 'N' AND TP.USE_YN = 'Y'
AND TP.SITE_NO = #{siteNo}
AND TP.APPLY_END_DTTM > sysdate

/* promotion.exhibition.selectTotalGoodsList - 기획전대상 등록 : 전체상품 적용일 경우 : 전체 상품 조회*/
SELECT GOODS_NO
FROM TG_GOODS
WHERE SITE_NO = #{siteNo}
AND DEL_YN = 'N'

/* promotion.exhibition.selectOtherExhibition - 진행중인 기획전 조회*/ SELECT PRMT_NM, PRMT_NO, APPLY_START_DTTM, APPLY_END_DTTM, CASE WHEN APPLY_START_DTTM > sysdate THEN '진행전' WHEN (APPLY_START_DTTM <= sysdate AND sysdate < APPLY_END_DTTM) THEN '진행중' WHEN APPLY_END_DTTM <= sysdate THEN '종료' END AS PRMT_STATUS_NM, REG_DTTM, PRMT_DC_VALUE FROM TP_PROMOTION WHERE DEL_YN = 'N' AND SITE_NO = #{siteNo}
AND APPLY_START_DTTM <= sysdate AND sysdate < APPLY_END_DTTM
ORDER BY REG_DTTM DESC

/* promotion.exhibition.selectExhibitionByGoods - 상품번호/카테고리로 기획전 조회*/ SELECT * FROM ( SELECT TP.PRMT_NO, TP.PRMT_NM, TP.PRMT_TYPE_CD, TP.PRMT_DSCRT, TP.APPLY_START_DTTM, TP.APPLY_END_DTTM, TP.PRMT_CONTENT_HTML, TP.PRMT_WEB_BANNER_IMG, TP.PRMT_MOBILE_BANNER_IMG, TP.PRMT_DC_VALUE, TP.PRMT_DC_GB_CD, TP.FIRST_BUY_SPC_PRICE, TP.USE_YN, TP.DEL_YN FROM TP_PROMOTION TP, TP_PROMOTION_TARGET_GOODS TPTG WHERE TP.PRMT_NO = TPTG.PRMT_NO AND TP.PRMT_GOODS_GB_CD ='01' AND TPTG.GOODS_NO = #{goodsNo} AND TP.USE_YN = 'Y' AND TP.DEL_YN = 'N' AND TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:SS') BETWEEN TO_CHAR(TP.APPLY_START_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TO_CHAR(TP.APPLY_END_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TP.SITE_NO = #{siteNo}
UNION ALL SELECT TP.PRMT_NO, TP.PRMT_NM, TP.PRMT_TYPE_CD, TP.PRMT_DSCRT, TP.APPLY_START_DTTM, TP.APPLY_END_DTTM, TP.PRMT_CONTENT_HTML, TP.PRMT_WEB_BANNER_IMG, TP.PRMT_MOBILE_BANNER_IMG, TP.PRMT_DC_VALUE, TP.PRMT_DC_GB_CD, TP.FIRST_BUY_SPC_PRICE, TP.USE_YN, TP.DEL_YN FROM TP_PROMOTION TP WHERE TP.PRMT_GOODS_GB_CD ='02' AND TP.PRMT_BRAND_NO= #{prmtBrandNo} AND TP.USE_YN = 'Y' AND TP.DEL_YN = 'N' AND TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:SS') BETWEEN TO_CHAR(TP.APPLY_START_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TO_CHAR(TP.APPLY_END_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TP.SITE_NO = #{siteNo}
ORDER BY PRMT_DC_VALUE DESC ) WHERE ROWNUM = 1

/* promotion.exhibition.selectEhbDispGoodsList - 진행중인 기획전 전시존의 상품 조회*/ SELECT TSDG.PRMT_DISPZONE_NO, TSDG.GOODS_NO, fn_getGoodsImgPath(TSDG.GOODS_NO, '02') AS GOODS_IMG_02, fn_getGoodsImgPath(TSDG.GOODS_NO, '03') AS GOODS_IMG_03, fn_getGoodsImgPath(TSDG.GOODS_NO, '04') AS GOODS_IMG_04, fn_getGoodsImgPath(TSDG.GOODS_NO, '05') AS GOODS_IMG_05, fn_getGoodsImgPath(TSDG.GOODS_NO, '06') AS GOODS_IMG_06, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'A') AS GOODS_DISP_IMG_A, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'B') AS GOODS_DISP_IMG_B, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'C') AS GOODS_DISP_IMG_C, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'D') AS GOODS_DISP_IMG_D, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'E') AS GOODS_DISP_IMG_E, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'F') AS GOODS_DISP_IMG_F, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'G') AS GOODS_DISP_IMG_G, fn_getGoodsDispImgPath(TSDG.GOODS_NO, 'S') AS GOODS_DISP_IMG_S, TB.BRAND_NO, TB.BRAND_NM, TG.GOODS_NM, TG.PR_WORDS, TI.CUSTOMER_PRICE, TI.SALE_PRICE, decode(nvl(TI.CUSTOMER_PRICE,0), 0, 0, 100-(TI.SALE_PRICE/TI.CUSTOMER_PRICE)*100) as SALE_RATE, fn_getGoodsIcon(TG.GOODS_NO, '5') AS ICON_IMGS, TG.GOODS_SVMN_AMT, TG.GOODS_SCORE, TG.ACCM_GOODSLETT_CNT, TG.RSV_ONLY_YN, TG.PRE_GOODS_YN, TG.STAMP_YN, NVL( (SELECT MAX(PRMT_DC_VALUE) FROM TP_PROMOTION TP, TP_PROMOTION_TARGET_GOODS TPTG WHERE TP.PRMT_NO = TPTG.PRMT_NO AND TPTG.GOODS_NO = TG.GOODS_NO AND TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:SS') BETWEEN TO_CHAR(TP.APPLY_START_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TO_CHAR(TP.APPLY_END_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TP.USE_YN = 'Y' AND TP.DEL_YN = 'N') ,0) AS PRMT_DC_VALUE, (SELECT MAX(PRMT_DC_GB_CD) FROM TP_PROMOTION TP, TP_PROMOTION_TARGET_GOODS TPTG WHERE TP.PRMT_NO = TPTG.PRMT_NO AND TPTG.GOODS_NO = TG.GOODS_NO AND TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:SS') BETWEEN TO_CHAR(TP.APPLY_START_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TO_CHAR(TP.APPLY_END_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TP.USE_YN = 'Y' AND TP.DEL_YN = 'N') AS PRMT_DC_GB_CD, (SELECT MAX(PRMT_TYPE_CD) FROM TP_PROMOTION TP, TP_PROMOTION_TARGET_GOODS TPTG WHERE TP.PRMT_NO = TPTG.PRMT_NO AND TPTG.GOODS_NO = TG.GOODS_NO AND TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:SS') BETWEEN TO_CHAR(TP.APPLY_START_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TO_CHAR(TP.APPLY_END_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TP.USE_YN = 'Y' AND TP.DEL_YN = 'N') AS PRMT_TYPE_CD, (SELECT MAX(FIRST_BUY_SPC_PRICE) FROM TP_PROMOTION TP, TP_PROMOTION_TARGET_GOODS TPTG WHERE TP.PRMT_NO = TPTG.PRMT_NO AND TPTG.GOODS_NO = TG.GOODS_NO AND TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:SS') BETWEEN TO_CHAR(TP.APPLY_START_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TO_CHAR(TP.APPLY_END_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TP.USE_YN = 'Y' AND TP.DEL_YN = 'N') AS FIRST_BUY_SPC_PRICE, (SELECT fn_getCuoponAvailInfo(TSDG.GOODS_NO,'CPAVL','') FROM DUAL) AS COUPON_AVL_INFO FROM TP_PROMOTION_DISPZONE TSDS JOIN TP_PROMOTION_TARGET_GOODS TSDG ON TSDS.PRMT_DISPZONE_NO = TSDG.PRMT_DISPZONE_NO AND TSDS.PRMT_NO = TSDG.PRMT_NO JOIN TG_GOODS TG ON TSDG.GOODS_NO = TG.GOODS_NO JOIN TG_ITEM TI ON TG.ITEM_NO = TI.ITEM_NO LEFT OUTER JOIN TG_BRAND TB ON TB.BRAND_NO = TG.BRAND_NO LEFT OUTER JOIN TG_GOODS_IMG_SET TGI ON TG.GOODS_NO = TGI.GOODS_NO AND TGI.DLGT_IMG_YN = 'Y' WHERE TSDS.DEL_YN = 'N' AND TSDS.USE_YN = 'Y' AND TSDS.PRMT_NO = #{prmtNo} AND TSDS.PRMT_DISPZONE_NO = #{prmtDispzoneNo}
AND (TG.SALE_START_DT <= TO_CHAR(sysdate, 'YYYYMMDD') OR TG.SALE_START_DT IS NULL) AND (TG.SALE_END_DT >= TO_CHAR(sysdate, 'YYYYMMDD') OR TG.SALE_END_DT IS NULL) AND TG.DISP_YN = 'Y' AND TG.GOODS_SALE_STATUS_CD = '1'
AND TG.SALE_YN = #{saleYn}
ORDER BY TSDG.APPLY_SEQ

/* promotion.exhibition.selectExhibitionTargetGoodsExist */ SELECT NVL(COUNT(MAX(BB.GOODS_NO)), 0) FROM (SELECT GDS.GOODS_NO ,TP.PRMT_NO FROM TG_GOODS GDS , TP_PROMOTION TP , TP_PROMOTION_TARGET_GOODS TPTG , TG_ITEM ITM LEFT OUTER JOIN TI_ERP_PRD_MAPPING TPM ON TPM.MALL_GOODS_NO = ITM.GOODS_NO AND TPM.MALL_ITM_CODE = ITM.ITEM_NO WHERE GDS.ITEM_NO = ITM.ITEM_NO AND GDS.SITE_NO = #{siteNo} AND GDS.DEL_YN = 'N' AND ITM.DEL_YN = 'N' AND PRMT_DC_VALUE > 0
AND GDS.GOODS_NO = #{goodsNo}
AND GDS.GOODS_NO IN #{item}
AND GDS.GOODS_NO = TPTG.GOODS_NO AND TPTG.PRMT_NO = TP.PRMT_NO AND TP.USE_YN = 'Y' AND TP.DEL_YN = 'N' AND TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:SS') BETWEEN TO_CHAR(TP.APPLY_START_DTTM,'YYYY-MM-DD HH24:MI:SS') AND TO_CHAR(TP.APPLY_END_DTTM,'YYYY-MM-DD HH24:MI:SS') ) BB
where BB.PRMT_NO <> #{prmtNo}
GROUP BY BB.GOODS_NO
